START TRANSACTION;
CREATE TABLE master."Districts" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "DistrictCode" character varying(10) NOT NULL,
    "DistrictName" character varying(100) NOT NULL,
    "DistrictNameFrench" character varying(100),
    "Region" character varying(50) NOT NULL,
    "AreaSqKm" numeric,
    "Population" integer,
    "DisplayOrder" integer NOT NULL,
    "IsActive" boolean NOT NULL,
    "CreatedAt" timestamp with time zone NOT NULL,
    "UpdatedAt" timestamp with time zone,
    "IsDeleted" boolean NOT NULL,
    CONSTRAINT "PK_Districts" PRIMARY KEY ("Id")
);

CREATE TABLE master."Villages" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "VillageCode" character varying(10) NOT NULL,
    "VillageName" character varying(200) NOT NULL,
    "VillageNameFrench" character varying(200),
    "PostalCode" character varying(10) NOT NULL,
    "DistrictId" integer NOT NULL,
    "LocalityType" character varying(50),
    "Latitude" numeric,
    "Longitude" numeric,
    "DisplayOrder" integer NOT NULL,
    "IsActive" boolean NOT NULL,
    "CreatedAt" timestamp with time zone NOT NULL,
    "UpdatedAt" timestamp with time zone,
    "IsDeleted" boolean NOT NULL,
    CONSTRAINT "PK_Villages" PRIMARY KEY ("Id"),
    CONSTRAINT "FK_Villages_Districts_DistrictId" FOREIGN KEY ("DistrictId") REFERENCES master."Districts" ("Id") ON DELETE RESTRICT
);

CREATE TABLE master."PostalCodes" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "Code" character varying(10) NOT NULL,
    "VillageName" character varying(200) NOT NULL,
    "DistrictName" character varying(100) NOT NULL,
    "Region" character varying(50) NOT NULL,
    "VillageId" integer NOT NULL,
    "DistrictId" integer NOT NULL,
    "LocalityType" character varying(50),
    "IsPrimary" boolean NOT NULL,
    "Notes" character varying(500),
    "IsActive" boolean NOT NULL,
    "CreatedAt" timestamp with time zone NOT NULL,
    "UpdatedAt" timestamp with time zone,
    "IsDeleted" boolean NOT NULL,
    CONSTRAINT "PK_PostalCodes" PRIMARY KEY ("Id"),
    CONSTRAINT "FK_PostalCodes_Districts_DistrictId" FOREIGN KEY ("DistrictId") REFERENCES master."Districts" ("Id") ON DELETE RESTRICT,
    CONSTRAINT "FK_PostalCodes_Villages_VillageId" FOREIGN KEY ("VillageId") REFERENCES master."Villages" ("Id") ON DELETE RESTRICT
);

CREATE INDEX "IX_Districts_DisplayOrder" ON master."Districts" ("DisplayOrder");

