--
-- Name: refresh_all_materialized_views_corrected(); Type: FUNCTION; Schema: master; Owner: postgres
--

CREATE FUNCTION master.refresh_all_materialized_views_corrected() RETURNS TABLE(schema_name text, view_name text, refresh_time interval, status text)
    LANGUAGE plpgsql
    AS $$
DECLARE
    v_start_time TIMESTAMP;
    v_schema RECORD;
    v_view RECORD;
BEGIN
    FOR v_schema IN
        SELECT nspname
        FROM pg_namespace
        WHERE (nspname LIKE 'tenant_%' AND nspname != 'tenant_testcorp')
           OR nspname = 'master'
    LOOP
        FOR v_view IN
            SELECT schemaname, matviewname
            FROM pg_matviews
            WHERE schemaname = v_schema.nspname
        LOOP
            v_start_time := clock_timestamp();

            BEGIN
                EXECUTE format('REFRESH MATERIALIZED VIEW CONCURRENTLY %I.%I',
                              v_view.schemaname, v_view.matviewname);

                schema_name := v_view.schemaname;
                view_name := v_view.matviewname;
                refresh_time := clock_timestamp() - v_start_time;
                status := 'SUCCESS';
                RETURN NEXT;
            EXCEPTION WHEN OTHERS THEN
                schema_name := v_view.schemaname;
                view_name := v_view.matviewname;
                refresh_time := clock_timestamp() - v_start_time;
                status := 'ERROR: ' || SQLERRM;
                RETURN NEXT;
            END;
        END LOOP;
    END LOOP;
END;
$$;


