--
-- Name: capture_performance_snapshot(); Type: FUNCTION; Schema: monitoring; Owner: postgres
--

CREATE FUNCTION monitoring.capture_performance_snapshot() RETURNS integer
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$
DECLARE
    v_rows_inserted INTEGER;
    v_cache_hit_rate NUMERIC;
    v_active_connections INTEGER;
    v_max_connections INTEGER;
    v_active_tenant_count INTEGER;
    v_total_tenant_count INTEGER;
BEGIN
    -- Calculate cache hit rate
    SELECT ROUND(100.0 * SUM(blks_hit) / NULLIF(SUM(blks_hit + blks_read), 0), 2)
    INTO v_cache_hit_rate
    FROM pg_stat_database
    WHERE datname = current_database();

    -- Get connection metrics
    SELECT COUNT(*) FILTER (WHERE state = 'active')
    INTO v_active_connections
    FROM pg_stat_activity;

    SELECT setting::INTEGER
    INTO v_max_connections
    FROM pg_settings
    WHERE name = 'max_connections';

    -- Get tenant counts from master schema (Fortune 500 multi-tenant architecture)
    -- Active tenants: Status != Deleted (assuming Status=0 is Active based on typical enum patterns)
    SELECT COUNT(DISTINCT "Id")
    INTO v_active_tenant_count
    FROM master."Tenants"
    WHERE "IsDeleted" = FALSE;

    SELECT COUNT(*)
    INTO v_total_tenant_count
    FROM master."Tenants";

    -- Insert performance snapshot
    INSERT INTO monitoring.performance_metrics (
        captured_at,
        cache_hit_rate,
        active_connections,
        max_connections,
        connection_utilization_pct,
        active_tenant_count,
        total_tenant_count
    )
    VALUES (
        NOW(),
        v_cache_hit_rate,
        v_active_connections,
        v_max_connections,
        ROUND(100.0 * v_active_connections / NULLIF(v_max_connections, 0), 2),
        v_active_tenant_count,
        v_total_tenant_count
    );

    GET DIAGNOSTICS v_rows_inserted = ROW_COUNT;
    RETURN v_rows_inserted;
END;
$$;


