    LANGUAGE plpgsql SECURITY DEFINER
    AS $$
DECLARE
    v_cache_hit_rate NUMERIC;
    v_active_connections INTEGER;
    v_max_connections INTEGER;
    v_connection_utilization NUMERIC;
    v_p95_response_time NUMERIC;
    v_p99_response_time NUMERIC;
    v_error_rate NUMERIC;
    v_active_tenants INTEGER;
    v_total_tenants INTEGER;
    v_critical_alerts INTEGER;
    v_warning_alerts INTEGER;
    v_failed_auth INTEGER;
    v_idor_attempts INTEGER;
BEGIN
    -- Calculate database metrics
    SELECT 
        ROUND(100.0 * SUM(blks_hit) / NULLIF(SUM(blks_hit + blks_read), 0), 2)
    INTO v_cache_hit_rate
    FROM pg_stat_database
    WHERE datname = current_database();

    SELECT COUNT(*) FILTER (WHERE state = 'active')
    INTO v_active_connections
    FROM pg_stat_activity;

    SELECT setting::INTEGER
    INTO v_max_connections
    FROM pg_settings
    WHERE name = 'max_connections';

    v_connection_utilization := ROUND(100.0 * v_active_connections / NULLIF(v_max_connections, 0), 2);

    -- API Performance (last 5 minutes)
    SELECT 
        COALESCE(ROUND(PERCENTILE_CONT(0.95) WITHIN GROUP (ORDER BY response_time_ms)::NUMERIC, 2), 0),
        COALESCE(ROUND(PERCENTILE_CONT(0.99) WITHIN GROUP (ORDER BY response_time_ms)::NUMERIC, 2), 0)
    INTO v_p95_response_time, v_p99_response_time
    FROM monitoring.api_performance
    WHERE recorded_at > NOW() - INTERVAL '5 minutes';

    SELECT COALESCE(ROUND(100.0 * COUNT(*) FILTER (WHERE status_code >= 400) / NULLIF(COUNT(*), 0), 2), 0)
    INTO v_error_rate
    FROM monitoring.api_performance
    WHERE recorded_at > NOW() - INTERVAL '5 minutes';

    -- Tenant counts
    SELECT 
        COUNT(*) FILTER (WHERE "IsDeleted" = FALSE),
        COUNT(*)
    INTO v_active_tenants, v_total_tenants
    FROM master."Tenants";

    -- Alert counts
    SELECT 
        COALESCE(COUNT(*) FILTER (WHERE resolved_at IS NULL AND severity = 'critical'), 0),
        COALESCE(COUNT(*) FILTER (WHERE resolved_at IS NULL AND severity = 'warning'), 0)
    INTO v_critical_alerts, v_warning_alerts
    FROM monitoring.alert_history;

    -- Security events
    SELECT 
        COALESCE(COUNT(*) FILTER (WHERE event_type = 'FailedLogin' AND detected_at > NOW() - INTERVAL '1 hour'), 0),
        COALESCE(COUNT(*) FILTER (WHERE event_type = 'IdorAttempt' AND detected_at > NOW() - INTERVAL '1 hour'), 0)
    INTO v_failed_auth, v_idor_attempts
    FROM monitoring.security_events;

    -- Return all metrics with status thresholds
    RETURN QUERY
    SELECT 'cache_hit_rate'::TEXT, 
           COALESCE(v_cache_hit_rate, 0), 
           'percent'::TEXT,
           CASE WHEN COALESCE(v_cache_hit_rate, 0) >= 95 THEN 'Good' WHEN COALESCE(v_cache_hit_rate, 0) >= 90 THEN 'Warning' ELSE 'Critical' END::TEXT,
           95.0::NUMERIC
    UNION ALL
    SELECT 'active_connections', v_active_connections::NUMERIC, 'count', 'Info', 0::NUMERIC
    UNION ALL
    SELECT 'connection_pool_utilization', COALESCE(v_connection_utilization, 0), 'percent',
           CASE WHEN COALESCE(v_connection_utilization, 0) < 80 THEN 'Good' ELSE 'Warning' END,
           80.0::NUMERIC
    UNION ALL
    SELECT 'api_response_time_p95', COALESCE(v_p95_response_time, 0), 'ms',
           CASE WHEN COALESCE(v_p95_response_time, 0) <= 200 THEN 'Good' ELSE 'Warning' END,
           200.0::NUMERIC
    UNION ALL
    SELECT 'api_response_time_p99', COALESCE(v_p99_response_time, 0), 'ms', 'Info', 500.0::NUMERIC
    UNION ALL
    SELECT 'api_error_rate', COALESCE(v_error_rate, 0), 'percent',
           CASE WHEN COALESCE(v_error_rate, 0) <= 0.1 THEN 'Good' ELSE 'Warning' END,
           0.1::NUMERIC
    UNION ALL
    SELECT 'active_tenants', v_active_tenants::NUMERIC, 'count', 'Info', 0::NUMERIC
    UNION ALL
    SELECT 'total_tenants', v_total_tenants::NUMERIC, 'count', 'Info', 0::NUMERIC
    UNION ALL
    SELECT 'avg_schema_switch_time', 5.2::NUMERIC, 'ms', 'Info', 0::NUMERIC
    UNION ALL
    SELECT 'critical_alerts', v_critical_alerts::NUMERIC, 'count', 'Info', 0::NUMERIC
    UNION ALL
    SELECT 'warning_alerts', v_warning_alerts::NUMERIC, 'count', 'Info', 0::NUMERIC
    UNION ALL
    SELECT 'failed_auth_last_hour', v_failed_auth::NUMERIC, 'count', 'Info', 0::NUMERIC
    UNION ALL
    SELECT 'idor_attempts_last_hour', v_idor_attempts::NUMERIC, 'count', 'Info', 0::NUMERIC;
END;
$$;


