--
-- Name: ActiveQueries; Type: VIEW; Schema: master; Owner: postgres
--

CREATE VIEW master."ActiveQueries" AS
 SELECT pid,
    usename,
    application_name,
    client_addr,
    state,
    EXTRACT(epoch FROM (now() - query_start)) AS query_duration_seconds,
    EXTRACT(epoch FROM (now() - state_change)) AS state_duration_seconds,
    wait_event_type,
    wait_event,
    "left"(query, 200) AS query_preview,
        CASE
            WHEN (EXTRACT(epoch FROM (now() - query_start)) > (300)::numeric) THEN 'LONG RUNNING (>5min)'::text
            WHEN (EXTRACT(epoch FROM (now() - query_start)) > (60)::numeric) THEN 'SLOW (>1min)'::text
            WHEN (EXTRACT(epoch FROM (now() - query_start)) > (10)::numeric) THEN 'NORMAL'::text
            ELSE 'FAST'::text
        END AS query_status,
    (('SELECT pg_cancel_backend('::text || pid) || ');'::text) AS cancel_statement,
    (('SELECT pg_terminate_backend('::text || pid) || ');'::text) AS terminate_statement
   FROM pg_stat_activity
  WHERE ((datname = current_database()) AND (state <> 'idle'::text) AND (pid <> pg_backend_pid()))
  ORDER BY query_start;


