--
-- Name: CurrentLocks; Type: VIEW; Schema: master; Owner: postgres
--

CREATE VIEW master."CurrentLocks" AS
 SELECT l.locktype,
    l.database,
    (l.relation)::regclass AS relation_name,
    l.page,
    l.tuple,
    l.virtualxid,
    l.transactionid,
    l.classid,
    l.objid,
    l.objsubid,
    l.virtualtransaction,
    l.pid,
    l.mode,
    l.granted,
    a.usename,
    a.application_name,
    a.client_addr,
    a.state,
    a.query_start,
    "left"(a.query, 100) AS query_preview
   FROM (pg_locks l
     LEFT JOIN pg_stat_activity a ON ((l.pid = a.pid)))
  WHERE (l.database = ( SELECT pg_database.oid
           FROM pg_database
          WHERE (pg_database.datname = current_database())))
  ORDER BY l.pid, l.granted DESC;


