--
-- Name: DatabaseOverview; Type: VIEW; Schema: master; Owner: postgres
--

CREATE VIEW master."DatabaseOverview" AS
 SELECT current_database() AS database_name,
    pg_size_pretty(pg_database_size(current_database())) AS database_size,
    ( SELECT version() AS version) AS postgres_version,
    ( SELECT pg_settings.setting
           FROM pg_settings
          WHERE (pg_settings.name = 'max_connections'::text)) AS max_connections,
    ( SELECT count(*) AS count
           FROM pg_stat_activity) AS active_connections,
    ( SELECT count(*) AS count
           FROM pg_stat_activity
          WHERE (pg_stat_activity.state = 'active'::text)) AS active_queries,
    ( SELECT count(*) AS count
           FROM pg_stat_activity
          WHERE (pg_stat_activity.state = 'idle in transaction'::text)) AS idle_in_transaction,
    ( SELECT count(*) AS count
           FROM pg_tables
          WHERE (pg_tables.schemaname ~~ 'tenant_%'::text)) AS total_tables,
    ( SELECT count(*) AS count
           FROM pg_indexes
          WHERE ((pg_indexes.schemaname ~~ 'tenant_%'::text) OR (pg_indexes.schemaname = 'master'::name))) AS total_indexes,
    now() AS snapshot_time;


