--
-- Name: TransactionStats; Type: VIEW; Schema: master; Owner: postgres
--

CREATE VIEW master."TransactionStats" AS
 SELECT datname,
    xact_commit AS commits,
    xact_rollback AS rollbacks,
    (xact_commit + xact_rollback) AS total_transactions,
        CASE
            WHEN ((xact_commit + xact_rollback) = 0) THEN (0)::numeric
            ELSE round(((100.0 * (xact_rollback)::numeric) / ((xact_commit + xact_rollback))::numeric), 2)
        END AS rollback_ratio_percent,
    blks_read AS disk_blocks_read,
    blks_hit AS cache_blocks_hit,
    tup_returned AS rows_returned,
    tup_fetched AS rows_fetched,
    tup_inserted AS rows_inserted,
    tup_updated AS rows_updated,
    tup_deleted AS rows_deleted,
    conflicts,
    temp_files AS temp_files_created,
    pg_size_pretty(temp_bytes) AS temp_bytes,
    deadlocks,
    stats_reset
   FROM pg_stat_database
  WHERE (datname = current_database());


