--
-- Name: dashboard_summary; Type: MATERIALIZED VIEW; Schema: monitoring; Owner: postgres
--

CREATE MATERIALIZED VIEW monitoring.dashboard_summary AS
 SELECT now() AS snapshot_time,
    ( SELECT performance_metrics.cache_hit_rate
           FROM monitoring.performance_metrics
          ORDER BY performance_metrics.captured_at DESC
         LIMIT 1) AS current_cache_hit_rate,
    ( SELECT performance_metrics.active_connections
           FROM monitoring.performance_metrics
          ORDER BY performance_metrics.captured_at DESC
         LIMIT 1) AS current_connections,
    ( SELECT performance_metrics.connection_utilization_pct
           FROM monitoring.performance_metrics
          ORDER BY performance_metrics.captured_at DESC
         LIMIT 1) AS connection_utilization,
    ( SELECT percentile_cont((0.95)::double precision) WITHIN GROUP (ORDER BY ((api_performance.response_time_ms)::double precision)) AS percentile_cont
           FROM monitoring.api_performance
          WHERE (api_performance.recorded_at > (now() - '00:05:00'::interval))) AS api_p95_response_time,
    ( SELECT (count(*) * 12)
           FROM monitoring.api_performance
          WHERE (api_performance.recorded_at > (now() - '00:05:00'::interval))) AS api_requests_per_minute,
    ( SELECT round(((100.0 * (count(*) FILTER (WHERE (api_performance.status_code >= 400)))::numeric) / (NULLIF(count(*), 0))::numeric), 2) AS round
           FROM monitoring.api_performance
          WHERE (api_performance.recorded_at > (now() - '00:05:00'::interval))) AS api_error_rate_pct,
    ( SELECT count(DISTINCT tenant_activity.tenant_subdomain) AS count
           FROM monitoring.tenant_activity
          WHERE (tenant_activity.measured_at > (now() - '00:05:00'::interval))) AS active_tenants_count,
    ( SELECT count(*) AS count
           FROM monitoring.security_events
          WHERE ((security_events.detected_at > (now() - '01:00:00'::interval)) AND ((security_events.severity)::text = 'critical'::text))) AS critical_security_events_1h,
    ( SELECT count(*) AS count
           FROM monitoring.alert_history
          WHERE (alert_history.resolved_at IS NULL)) AS active_alerts_count
  WITH NO DATA;


ALTER MATERIALIZED VIEW monitoring.dashboard_summary OWNER TO postgres;

--
-- Name: MATERIALIZED VIEW dashboard_summary; Type: COMMENT; Schema: monitoring; Owner: postgres
--

COMMENT ON MATERIALIZED VIEW monitoring.dashboard_summary IS 'Aggregated dashboard metrics refreshed every minute for fast querying';


